create table "public"."supplier" (
    "id" bigint generated by default as identity not null,
    "name" text not null,
    "contact_person" text not null,
    "email" text not null,
    "phone" text,
    "address" text,
    "status" boolean not null default true,
    "inserted_at" timestamp with time zone not null default timezone('utc'::text, now()),
    "updated_at" timestamp with time zone not null default timezone('utc'::text, now())
);

CREATE UNIQUE INDEX supplier_email_key ON public.supplier USING btree (email);

CREATE UNIQUE INDEX supplier_pkey ON public.supplier USING btree (id);

alter table "public"."supplier" add constraint "supplier_pkey" PRIMARY KEY using index "supplier_pkey";

alter table "public"."supplier" add constraint "supplier_email_check" CHECK ((email ~* '^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$'::text)) not valid;

alter table "public"."supplier" validate constraint "supplier_email_check";

alter table "public"."supplier" add constraint "supplier_email_key" UNIQUE using index "supplier_email_key";

